adapter.queryTemplate:
DEFINE sql:select-option "order"
PREFIX  rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX  m2r:  <http://med2rdf.org/ontology/med2rdf#>
PREFIX  obo:  <http://purl.obolibrary.org/obo/>
PREFIX  dct: <http://purl.org/dc/terms/>
PREFIX  faldo: <http://biohackathon.org/resource/faldo#>
SELECT DISTINCT  (?variant as ?uniqueId) ?hgvs ?name ?variant_type ?so_type ?start ?stop ?end ?ref ?alt
FROM <http://togovar.biosciencedbc.jp/graph/variant>
FROM <http://togovar.biosciencedbc.jp/graph/so>
WHERE
  { ?variant  a                     ?_vt ;
              rdfs:label            ?hgvs ;
              faldo:location        ?_loc ;
              dct:identifier        ?name
    VALUES ?_vt { obo:SO_0001483 obo:SO_0000667 obo:SO_0000159 obo:SO_1000032 obo:SO_1000002 }
    ?_vt  rdfs:label  ?variant_type
      { ?_loc_begin  faldo:reference  <http://identifiers.org/hco/{refName}#GRCh37> ;
                  faldo:position   ?start .
        ?_loc     faldo:begin      ?_loc_begin .
        ?_loc faldo:end/faldo:position ?stop .
      }
    UNION
      { ?_loc  faldo:reference  <http://identifiers.org/hco/{refName}#GRCh37> ;
               faldo:position   ?start
      }
    FILTER ( ( ( ?start > {start} ) && ( ?start < {end} ) ) )
    OPTIONAL
      { ?variant  m2r:reference_allele  ?ref }
    OPTIONAL
      { ?variant  m2r:alternative_allele  ?alt }
    BIND(replace(str(?_vt), ".*/", "") AS ?so_type)
    BIND(IF(?stop, ?stop, ?start + 1) AS ?end)
  }

PREFIX  rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX  rdf:  <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
PREFIX  faldo: <http://biohackathon.org/resource/faldo#>
PREFIX  dc:   <http://purl.org/dc/elements/1.1/>

SELECT DISTINCT  ?gene (?id AS ?uniqueId) (?label AS ?name) (?desc AS ?note) ?strand ?start ?end
WHERE
  { VALUES ?location_type { faldo:ForwardStrandPosition faldo:ReverseStrandPosition faldo:BothStrandsPosition }
    ?location  faldo:begin     _:b0 .
    _:b0      rdf:type         ?location_type ;
              faldo:position   ?faldo_begin .
    ?location  faldo:end       _:b1 .
    _:b1      rdf:type         ?location_type ;
              faldo:position   ?faldo_end .
    ?location  faldo:reference  <http://rdf.ebi.ac.uk/resource/ensembl/97/homo_sapiens/GRCh38/{refName}> .
    ?gene     rdf:type         ?type ;
              rdfs:label       ?label ;
              dc:description   ?desc ;
              dc:identifier    ?id ;
              faldo:location   ?location
    BIND(if(( ?location_type = faldo:ForwardStrandPosition ), 1, if(( ?location_type = faldo:ReverseStrandPosition ), -1, 0)) AS ?strand)
    BIND(if(( ?strand = -1 ), ?faldo_end, ?faldo_begin) AS ?start)
    BIND(if(( ?strand = -1 ), ?faldo_begin, ?faldo_end) AS ?end)
    FILTER ( ( ?start >= {start} ) && ( ?end <= {end} ) )
  }
